function registerCommand(commandName,handlerFunction,config,restricted){setImmediate(()=>{emit("chat:addSuggestion",`/${commandName}`,config?.description,config.arguments)});RegisterCommand(commandName,(source,args,raw)=>{if(config.adminLevel){}console.log("trigger");handlerFunction(source,args,raw)},restricted)}registerCommand("spawnveh",(source,args,raw)=>{if(!args[0]){console.error("ERROR! You should use /spawnveh <vehicleName> <color>");return}const model=args[0].toString();const playerPosition=GetEntityCoords(GetPlayerPed(source));const createdVehicle=CreateVehicle(model,playerPosition[0],playerPosition[1],playerPosition[2],0,true,true);TaskWarpPedIntoVehicle(GetPlayerPed(source),createdVehicle,-1);SetVehicleCustomPrimaryColour(createdVehicle,0,0,0);SetVehicleCustomSecondaryColour(createdVehicle,0,0,0)},{description:"Spawn a vehicle at your position",arguments:[{name:"vehicleName",help:"The name of the vehicle model"},{name:"color",help:"The color of the spawned vehicle"}]},false);var AdminServerModule=class{constructor(){console.log("Admin Module (Server) Initialized")}};var server_default=AdminServerModule;export{server_default as default};
